package org.springframework;public class User {    private String name;    private String surname;    private  int age;    private double balance;    private int pass;    public User(String name, String surname, int age, double balance, int pass) {        this.name = name;        this.surname = surname;        this.age = age;        this.balance = balance;        this.pass = pass;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getSurname() {        return surname;    }    public void setSurname(String surname) {        this.surname = surname;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public double getBalance() {        return balance;    }    public void setBalance(double balance) {        this.balance = balance;    }    public int getPass() {        return pass;    }    public void setPass(int pass) {        this.pass = pass;    }    @Override    public String toString() {        return "User{" +                "name='" + name + '\'' +                ", surname='" + surname + '\'' +                ", age=" + age +                ", balance=" + balance +                ", pass=" + pass +                '}';    }}